/**
 * Footer
 */
export const Footer = [
  {
    size: { sm: 6, md: 3 },
    header: 'About',
    text: `
      <p>
        DryKISS is a full service internet and mobile digital production house.
      </p>

      <p>
        Our core services span consulting, strategy; planning; development;
        testing and analytics. Using cutting edge technologies within
        disciplined processes.
      </p>
      `,
    links: [
      {
        id: 'footerTeam',
        name: 'DryKISS Team',
        to: '/team'
      }
    ]
  },
  {
    size: { sm: 6, md: 3 },
    header: 'What We Do',
    text: `
      <p>
        DryKISS develops hybrid mobile apps (iOS &amp; Android); responsive
        internet sites; administration systems and social applications.
      </p>

      <p>
        Using our platform we tune for SEO, accessibility and conversion at the
        very start.
      </p>
    `
  },
  {
    size: { sm: 6, md: 3 },
    header: 'Social',
    links: [
      {
        id: 'footerFacebook',
        name: 'Facebook',
        to: 'https://www.facebook.com/DryKISSLtd'
      },
      {
        id: 'footerTwitter',
        name: 'Twitter',
        to: 'https://twitter.com/DryKISSLtd'
      },
      {
        id: 'footerLinkedIn',
        name: 'LinkedIn',
        to: 'https://www.linkedin.com/company/drykiss-ltd'
      }
    ],
    text: `
      We are experts in creating social media apps and pages.
    `
  },
  {
    size: { sm: 6, md: 3 },
    header: 'Contact',
    text: `
      <p>
        If you have a brief, and want to discuss how we would approach it.
      </p>

      <address>
        Wimbledon, London, UK
      </address>
    `,
    links: [
      {
        id: 'footerCall',
        name: '44 (0) 778 043 5329',
        to: 'tel:+447780435329'
      },
      {
        id: 'footerContact',
        name: 'Please get in touch',
        to: '/contact'
      },
      {
        id: 'footerEmail',
        name: 'info@drykiss.com',
        to: 'mailto:info@drykiss.com'
      }
    ]
  }
]
